import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Events from "../Events/Events";
import EventsComp from "../EventsComp";
import NotFound from "../NotFound";
import Topstyles from "./seperateEvent.module.css";
import { css } from "@emotion/react";
import ClipLoader from "react-spinners/ClipLoader";
export default function SeperateEvent(props) {
  const [Event, setEvent] = useState([]);
  const [Loaded,setLoaded]=useState(false)
  const [errorFetchedChecker, seterrorFetchedChecker] = useState(false);
  let { id } = useParams();

   useEffect(() => {
    async function fetchData(){
    await axios
      .post("http://localhost:3001/api/SeperateEvent", { EID: id })
      .then((response) => {
        console.log(response)
    if(response.data==="not found"){
      setLoaded(true)
        setEvent("");
     
      }
    else if(response.data.length>0){
      setLoaded(true)
      setEvent(response.data)
    }})
    .catch(err=>{
     
      seterrorFetchedChecker((c) => !c);
    });
  }
  if(errorFetchedChecker){
    setTimeout(()=>{
        fetchData();
    },[3000])// 1 second
} else {
    fetchData();
} }, [errorFetchedChecker]); 
  return (
    <div>
   {Loaded? (Event.length>0 ? <div> <div className={Topstyles.titleEng}>
        {" "}
        <p className={Topstyles.date}>Date: {props.date||Event[0].EDate}</p>
        <h1 className={Topstyles.title}>{props.title||Event[0].ETitle}</h1>
        <p>{props.text2||Event[0].EBrief}</p>
      </div>
      <EventsComp img={props.img||Event[0].EPhotos} /></div>:  <NotFound/>) :<div className={Topstyles.spinnerdiv}><ClipLoader color={"#ffc41d"} size={150} className={Topstyles.spinner}/></div> }
    
     
    </div>
  );
}
