import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Events from "../Events/Events";
import EventsComp from "../EventsComp";
import Topstyles from "./seperateEvent.module.css";
export default function SeperateEvent(props) {
  const [Event, setEvent] = useState([]);
  let { id } = useParams();

   useEffect(() => {
    async function fetchData(){
    await axios
      .post("http://localhost:3001/api/SeperateEvent", { EID: id })
      .then((response) => {
    
        setEvent(response.data[0]);
        console.log(response.data);
      });
  }fetchData()}, []); 
  return (
    <div>
      {/* <h1>{Event.ETitle}</h1> */}
      {Event.length>0 ? <div> <div className={Topstyles.titleEng}>
        {" "}
        <p className={Topstyles.date}>Date: {props.date||Event.EDate}</p>
        <h1 className={Topstyles.title}>{props.title||Event.ETitle}</h1>
        <p>{props.text2||Event.EBrief}</p>
      </div>
      <EventsComp img={props.img||Event.EPhotos} /></div>:""}
     
    </div>
  );
}
